sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    g_form.clearMessages();

    // get starting date
    var dt1 = g_form.getValue('from');
    var new_dt1 = dt1[5] + dt1[6] + '/' + dt1[8] + dt1[9] + '/' + dt1[0] + dt1[1] + dt1[2] + dt1[3];
    var date_from = new Date(new_dt1);

    // get today's date
    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();

    today = mm + '/' + dd + '/' + yyyy;
	var td = new Date(today);

    // get difference between two dates
    var diff_ms = date_from.getTime(); - td.getTime();
    var diff_days = diff_ms / (1000 * 3600 * 24) + 1;

    //display info message if starting date is too close to today
    if (diff_days > 0 && diff_days <= 3) {
        g_form.hideFieldMsg('from', ture);
        g_form.showFieldMsg('The start date is too close to today. There may not be enought time for approval.');
    }


}
