sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // get user input dates and convert to JS format
    var dt1 = g_form.getValue('from');
    var dt2 = g_form.getValue('to');

    var new_dt1 = dt1[5] + dt1[6] + '/' + dt1[8] + dt1[9] + '/' + dt1[0] + dt1[1] + dt1[2] + dt1[3];
    var new_dt2 = dt2[5] + dt2[6] + '/' + dt2[8] + dt2[9] + '/' + dt2[0] + dt2[1] + dt2[2] + dt2[3];

    // create JS Date() object
    var date1 = new Date(new_dt1);
    var date2 = new Date(new_dt2);

    // To calculate the time difference of two dates in milliseconds
    var diff_ms = date2.getTime() - date1.getTime();

    // To calculate the no. of days between two dates, starting day included
    var diff_days = diff_ms / (1000 * 3600 * 24) + 1;
    if (diff_days <= 0) {
        g_form.addErrorMessage('Invalid dates. The To field must be a further date than the From field.');
        g_form.setValue('days', 'Invalid inputs');
    } else {
        //set days field value
        g_form.clearMessages();
        g_form.setValue('days', diff_days);
    }

}
