sys_script_include.script:
var TravelUtil = Class.create();
TravelUtil.prototype = {
    initialize: function() {
    },

    daysBeforeStart: function(stdate) {
        var today = new GlideDateTime().getLocalDate();
        var diff = new GlideDuration();

        if (today == stdate) {
            diff = 1;
        } else if (today < stdate) {
            diff = GlideDateTime.subtract(today, stdate).getDayPart() + 1;
        }else{return;}

        return diff;
    },

    type: 'TravelUtil'
};
