sysauto_script.script:
// query all awaiting approval records
var approvalNeeded = new GlideRecord('x_530258_my_travel_travels');
approvalNeeded.addQuery('state', '=', 22).addOrCondition('state', '=', 23);

approvalNeeded.query();

while (approvalNeeded.next()) {
	// to get start date
    var start_gdt = new GlideDateTime(approvalNeeded.getDisplayValue('from'));

	// to get days before start date
    var dur = new TravelUtil().daysBeforeStart(start_gdt);


    if (dur > 0 && dur <= 10) {
        approvalNeeded.urgency = 1;
        approvalNeeded.approval_history = 'Auto update: Urgency has been set from Medium to High.';
		gs.info('Urgency updated from Medium to High on Travel Request: ' + approvalNeeded.getValue('number') + '.');
    } else if (dur <= 30) {
        approvalNeeded.urgency = 2;
        approvalNeeded.approval_history = 'Auto update: Urgency has been set from Low to Medium.';
		gs.info('Urgency updated from Low to Medium on Travel Request: ' + approvalNeeded.getValue('number') + '.');
    } else if (dur > 30) {
        approvalNeeded.urgency = 3;
    } else {
        approvalNeeded.state = 7;
        approvalNeeded.approval_history = "Auto update: Record State has been set to Failed to Approve because it was not approved before start date.";
    }
    approvalNeeded.update();
}
